
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* Ciclo o estructuras repetibles */
		/* While do-While, For, Foreach , entre otros */
		/*
		 * int num = 0; while (num < 10) { // true o false
		 * System.out.println("Hola Mundo"); num++;
		 * 
		 * }
		 * 
		 */
		// caracteristica principal de los ciclos
		// Finito -> finalizar

		/*
		 * CICLO int num = 0; do { // instruciones System.out.println("Hola"); num++; }
		 * while (num < 10);
		 */

		// CICLO FOR-PARA
		for (int num = 0; num < 10; num++) {
			System.out.println("HOLA MUNDO CON UN CICLO FOR");

		}

	}

}
