
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 5;
		
		//if-else sentencia una u otra através de una condición
		//num < 10 = true
		//num > 10 = false;
		
		if (num < 10) { //true false
			//instrucciones
			System.out.println("El numero es menor a 10");
		
		}else {
		 //solo ejecuta instrucciones dentro del else	
		System.out.println("El numero no es menor a 10");
		}
		
	}

}
