
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * Switch case: es una estructura de control que permite definir varios casos de
		 * seleccion
		 */

		int var = 2;
		String cadena = "C";
		switch (cadena) {
		case "A":
			// instrucciones
			System.out.println("La variable Contiene A");
			break;

		case "B":
			// instrucciones
			System.out.println("La variable Contiene B");
			break;

		case "C":
			// instrucciones
			System.out.println("La variable Contiene C");
			break;

		default:
			System.out.println("La variable es diferente de A, B O C");
			break;
		}

	}

}
